import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [clave, setClave] = useState('');
  const [mostrarClave, setMostrarClave] = useState(false);
  const navigate = useNavigate();

  const USUARIO_VALIDO = "admin";
  const PASSWORD_VALIDO = "pulsera123";

  // ▶️ Reproducir sonido al cargar la página
  useEffect(() => {
    const audio = new Audio('/intro.mp3'); // archivo debe estar en /public
    audio.play().catch((e) => {
      console.warn("Audio bloqueado por navegador. Se necesita interacción.");
    });
  }, []);

  const handleLogin = () => {
    if (usuario === USUARIO_VALIDO && clave === PASSWORD_VALIDO) {
      navigate('/app');
    } else {
      alert("Usuario o contraseña incorrectos");
    }
  };

  const habilitarBoton = usuario.trim() !== '' && clave.trim() !== '';

  return (
    <div className="login-container">
      <video autoPlay muted loop className="logo-video">
        <source src="/logoo.mp4" type="video/mp4" />
        Tu navegador no soporta video.
      </video>

      <div className="login-form">
        <div className="input-container">
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
          />
        </div>

        <div className="input-container">
          <input
            type={mostrarClave ? "text" : "password"}
            placeholder="Contraseña"
            value={clave}
            onChange={(e) => setClave(e.target.value)}
          />
          <i
            className={`fas ${mostrarClave ? "fa-eye-slash" : "fa-eye"} toggle-icon`}
            onClick={() => setMostrarClave(!mostrarClave)}
          ></i>
        </div>

        {habilitarBoton && (
          <button className="fade-in" onClick={handleLogin}>Iniciar</button>
        )}
      </div>
    </div>
  );
};

export default Login;
