import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [clave, setClave] = useState('');
  const navigate = useNavigate();

 const PASSWORD = "pulsera123"; // contraseña predeterminada
  const handleLogin = () => {
  if (clave === PASSWORD) {
    navigate('/app');
  } else {
    alert("Contraseña incorrecta");
  }
};

  const habilitarBoton = usuario.trim() !== '' && clave.trim() !== '';

  return (
    <div className="login-container">
      <video autoPlay muted loop className="logo-video">
        <source src="/logoo.mp4" type="video/mp4" />
        Tu navegador no soporta video.
      </video>

      <div className="login-form">
        <div className="input-container">
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
          />
          {usuario && <i className="fas fa-check-circle valid-icon"></i>}
        </div>
        <div className="input-container">
          <input
            type="password"
            placeholder="Contraseña"
            value={clave}
            onChange={(e) => setClave(e.target.value)}
          />
          {clave && <i className="fas fa-check-circle valid-icon"></i>}
        </div>
        {habilitarBoton && (
          <button className="fade-in" onClick={handleLogin}>Iniciar</button>
        )}
      </div>
    </div>
  );
};

export default Login;
