import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import DevicePanel from './components/DevicePanel';

const App = () => {
  const [dispositivos, setDispositivos] = useState([]);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyA-JDW1_wZjD9uXS_v-LpOAs8YXb1E7FhI'
  });

  useEffect(() => {
    const fetchUbicaciones = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/ubicacion');
        setDispositivos(res.data);
      } catch (err) {
        console.error('Error al obtener datos:', err);
      }
    };

    fetchUbicaciones();
    const intervalo = setInterval(fetchUbicaciones, 10000); // cada 10s

    return () => clearInterval(intervalo);
  }, []);

  return isLoaded ? (
    <div style={{ display: 'flex' }}>
      <DevicePanel dispositivos={dispositivos} />
      <GoogleMap
        center={dispositivos[0] || { lat: 19.4326, lng: -99.1332 }}
        zoom={14}
        mapContainerStyle={{ width: '100%', height: '100vh' }}
      >
        {dispositivos.map((d, i) => (
          <Marker
            key={i}
            position={{ lat: d.latitud, lng: d.longitud }}
            label={d.dispositivoId}
          />
        ))}
      </GoogleMap>
    </div>
  ) : (
    <div>Cargando mapa...</div>
  );
};

export default App;
